import React from 'react'
import TitleSectionText from './ui/TitleSectionText'
import Button from './Button'
import Icon from './ui/Icon'
import { FaArrowRight, FaUsers, FaChartLine, FaDollarSign, FaShieldAlt, FaClock, FaStar } from 'react-icons/fa'

const Component9 = ({
  tagText = null,
  titlePart1 = "Why Partner with <span>FreshFold?</span>",
  description = "We provide everything you need to succeed in the laundry business.",
  ctaButtonText = "Hire Us Today",
  cards = [
    {
      icon: FaUsers,
      title: "Reach More Customers",
      description: "Get discovered by thousands of customers actively looking for laundry services."
    },
    {
      icon: FaChartLine,
      title: "Grow Your Business",
      description: "Expand your customer base and increase your revenue with our marketing tools."
    },
    {
      icon: FaDollarSign,
      title: "Competitive Pricing",
      description: "Set your own prices and keep more of what you earn with low platform fees."
    },
    {
      icon: FaShieldAlt,
      title: "Secure Payments",
      description: "Get paid on time with our secure payment processing system."
    },
    {
      icon: FaClock,
      title: "Flexible Schedule",
      description: "Work on your own terms. Accept orders when it suits your business."
    },
    {
      icon: FaStar,
      title: "Build Your Reputation",
      description: "Collect reviews and ratings to stand out from the competition."
    }
  ],
  onCtaClick,
  onCardLinkClick
}) => {

  return (
    <div className="bg-[#F7F9FA] py-16 px-4">
      <div className="container mx-auto">
        {/* Header Section */}
        <div className="mb-12">
          <TitleSectionText
            tagText={tagText}
            titlePart1={titlePart1}
            description={description}
          />
        </div>

        {/* Service Cards Grid - Single Row */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mt-12 mb-12">
          {cards.map((card, index) => (
            <div
              key={index}
              className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-200 relative"
            >
              {/* Gradient Icon - Top Left, Overlapping */}
              <div className={`absolute -top-3 -left-3 w-14 h-14 rounded-2xl  ${gradientIcons[card.icon] || gradientIcons["gradient-pink-orange"]} flex items-center justify-center shadow-md z-10`}>
                <div className="w-8 h-8 bg-white rounded opacity-20 flex items-center justify-center">
                  {
                    card.icon
                  }
                </div>
              </div>

              {/* Content */}
              <div className="pt-4">
                {/* Title */}
                <h3 className="text-xl font-bold text-[#1E2A36] mb-3">
                  {card.title}
                </h3>

                {/* Description */}
                <p className="text-[#1E2A36] leading-relaxed mb-4 text-sm">
                  {card.description}
                </p>

                {/* Read More Link */}
                <button
                  onClick={() => onCardLinkClick && onCardLinkClick(index)}
                  className={`text-sm font-medium flex items-center gap-1 hover:gap-2 transition-all ${
                    card.linkColor === "pink" 
                      ? "text-pink-500" 
                      : "text-[#62707D]"
                  }`}
                >
                  {card.linkText}
                  <Icon 
                    icon={FaArrowRight} 
                    theme={card.linkColor === "pink" ? "primary" : "dark"} 
                    size="sm" 
                    className={card.linkColor === "pink" ? "text-pink-500" : "text-[#62707D]"} 
                  />
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* CTA Button */}
        <div className="text-center mt-8">
          <Button
            variant="primary"
            icon={<Icon icon={FaArrowRight} theme="light" size="sm" />}
            onClick={onCtaClick}
            className="mx-auto"
          >
            {ctaButtonText}
          </Button>
        </div>
      </div>
    </div>
  )
}

export default Component9
