import React from "react"
import Tag from "./Tag"           // adjust path if needed

const TitleSectionText = ({
  tagText,
  titlePart1,
  titlePart2,
  description,
}) => {
  return (
    <div className="space-y-6">
      {/* Tag */}
      <div>
        <Tag>{tagText}</Tag>
      </div>

      {/* Headline */}
      <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold leading-tight" dangerouslySetInnerHTML={{__html : titlePart1}} />

      {/* Description */}
      <p className="text-lg text-[#62707D] leading-relaxed max-w-xl">
        {description}
      </p>


    </div>
  )
}

export default TitleSectionText
