import React from 'react'
import TitleSectionText from './ui/TitleSectionText'
import Button from './Button'
import Icon from './ui/Icon'
import { FaArrowRight } from 'react-icons/fa'

const Component9 = ({
  tagText = null,
  titlePart1 = "Our <span class='highlight-pink'>Services</span>",
  description = "Our mission is to drive progress and enhance the lives of our customers by delivering superior products and services that exceed expectations.",
  ctaButtonText = "Hire Us Today",
  cards = [
    {
      icon: "gradient-pink-orange",
      title: "Title Goes Here",
      description: "Tailored to meet individual needs perfectly balanced.",
      linkText: "Read more",
      linkColor: "pink"
    },
    {
      icon: "gradient-green-teal",
      title: "Title Goes Here",
      description: "Tailored to meet individual needs perfectly balanced.",
      linkText: "Read more",
      linkColor: "gray"
    },
    {
      icon: "gradient-blue-purple",
      title: "Title Goes Here",
      description: "Tailored to meet individual needs perfectly balanced.",
      linkText: "Read more",
      linkColor: "gray"
    },
    {
      icon: "gradient-orange-yellow",
      title: "Title Goes Here",
      description: "Tailored to meet individual needs perfectly balanced.",
      linkText: "Read more",
      linkColor: "gray"
    }
  ],
  onCtaClick,
  onCardLinkClick
}) => {
  const gradientIcons = {
    "gradient-pink-orange": "bg-gradient-to-br from-pink-500 to-orange-500",
    "gradient-green-teal": "bg-gradient-to-br from-green-400 to-teal-500",
    "gradient-blue-purple": "bg-gradient-to-br from-blue-400 to-purple-500",
    "gradient-orange-yellow": "bg-gradient-to-br from-orange-400 to-yellow-400"
  }

  return (
    <div className="bg-[#2C2C2C] py-16 px-4">
      <div className="container mx-auto">
        {/* Light Grey Rounded Container */}
        <div className="bg-[#F7F9FA] rounded-3xl p-8 md:p-12 lg:p-16">
          {/* Header Section */}
          <div className="text-center mb-12">
            <TitleSectionText
              tagText={tagText}
              titlePart1={titlePart1}
              description={description}
              descriptionClass="mx-auto"
            />
          </div>

          {/* Service Cards Grid - 2x2 */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-12 mb-12">
            {cards.map((card, index) => (
              <div
                key={index}
                className="bg-white rounded-xl shadow-sm p-6 hover:shadow-md transition-shadow duration-200 relative"
              >
                {/* Gradient Icon - Top Left, Overlapping */}
                <div className={`absolute -top-3 -left-3 w-16 h-16 rounded-full ${gradientIcons[card.icon] || gradientIcons["gradient-pink-orange"]} flex items-center justify-center shadow-lg`}>
                  <div className="w-8 h-8 bg-white rounded-full opacity-20"></div>
                </div>

                {/* Content */}
                <div className="mt-8">
                  {/* Title */}
                  <h3 className="text-xl font-bold text-[#1E2A36] mb-3">
                    {card.title}
                  </h3>

                  {/* Description */}
                  <p className="text-[#1E2A36] leading-relaxed mb-4">
                    {card.description}
                  </p>

                  {/* Read More Link */}
                  <button
                    onClick={() => onCardLinkClick && onCardLinkClick(index)}
                    className={`text-sm font-medium flex items-center gap-1 hover:gap-2 transition-all ${
                      card.linkColor === "pink" 
                        ? "text-pink-500" 
                        : "text-[#1E2A36]"
                    }`}
                  >
                    {card.linkText}
                    <Icon icon={FaArrowRight} theme="dark" size="sm" className={card.linkColor === "pink" ? "text-pink-500" : ""} />
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* CTA Button */}
          <div className="text-center mt-8">
            <Button
              variant="primary"
              icon={<Icon icon={FaArrowRight} theme="light" size="sm" />}
              onClick={onCtaClick}
              className="bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 text-white px-8 py-3 rounded-lg"
            >
              {ctaButtonText}
            </Button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Component9
